import React, { Component } from 'react';
import { Button, Toast } from 'antd-mobile'
import { register, sendCode, login, auth_mobile, change_pwd } from '../../services/user'
import './index.less'
class index extends Component {
	constructor(props) {
		super(props)

		this.state = {
			loginContent: true,
			registered: false,
			Retrieve: false,
			changePassWord: false,
			telNumber: '',
			codeNumber: '',
			passWord: '',
			rePassWord: '',
			getCode: '获取验证码',
			userIptFocus: false,
			pswIptFocus: false,
			codeIptFocus: false,
			rePswIptFocus:false
		}
	}
	initialization = () => {
		this.setState({
			loginContent: true,
			registered: false,
			Retrieve: false,
			changePassWord: false,
			telNumber: '',
			codeNumber: '',
			passWord: '',
			getCode: '获取验证码'
		})
		this.props.rightCallBack()
	}
	timeChange = () => {
		let num = 60
		const time = setInterval(() => {
			num--
			this.setState({
				getCode: `${num}秒重试`
			})
			if (num === 0) {
				clearInterval(time)
				this.setState({
					getCode: '重新获取'
				})
			}
		}, 1000)
	}
	geiCodeNumber = (event = 'register') => {
		const getCode = this.state.getCode
		const { telNumber } = this.state
		if (telNumber.length !== 11) {
			return false
		}
		if (getCode === '获取验证码' || getCode === '重新获取') {

			sendCode({
				mobile: telNumber,
				event,
				type: 'user'
			}).then(res => {
				if (res.code === 0) {
					Toast.info(res.suc)
					this.timeChange()
				} else {
					Toast.info(res.err)
				}
			})

		}
	}
	register = () => {
		const { telNumber, codeNumber, passWord } = this.state
		register({
			mobile: telNumber,
			password: passWord,
			code: codeNumber
